
public class BitXOR {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 170, b = 245, r;
		
		r = a ^ b;	// ∫Ò∆Æ XOR
				
		System.out.printf("a = %d = %X = 1010 1010\n",a,a);
		System.out.printf("b = %d = %X = 1111 0101\n",b,b);
		System.out.printf("r = %d = %X = 0101 1111\n",r,r);
	}

}
