
public class Bool {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10, b = 20;
		boolean c, d, e;	// true 또는 false 값을 가짐
		
		c = a <= b;
		d = a >= b;
		e = a == b;
		
		System.out.println("a= " +a+ ", b = " +b);
		System.out.println("a >= b : " +c);
		System.out.println("a <= b : " +d);
		System.out.println("a == b : " +e);
	}

}
