
public class Calc1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(" 7+7= " + (7+7));
		System.out.println(" 7+7= " + 7+7);
		System.out.println(" 7+7= " + (7+7)+7);
		System.out.println(" 7-7= " + (7-7));
		System.out.println(" 7*7= " + (7*7));
		System.out.println(" 7/7= " + (7/7));
		System.out.println(" 7%4= " + (7%4));
	}

}
