
public class Cast2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("실수/실수 = 실수 : 3 / 2 = " + 3.0/2.0);
		System.out.println("정수/실수 = 실수 : 3 / 2 = " + (int)3.0/2.0);
		System.out.println("(int)(실수/실수) = 정수 : 3 / 2 = " + (int)(3.0/2.0));
	}

}
