
public class CompareEr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10, b=20;;
		
		System.out.println("a= " +a+",b = " +b);
		System.out.println("a==b : " +(a == b));
		System.out.println("a==b : " +a == b);	// 비교 연산식을 ()로 묶어주어야함, 컴파일 에러 발생
}
}