
public class If_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 5; 
		
		if((x%2)==0)
		{	
			System.out.println(x + "는 짝수");				//조건식 결과가 거짓이므로 실행x
		}
		else
			System.out.println(x + "는 홀수");				//실행됨
		
		System.out.println("if 구조는 Java와 C언에서 비슷함");	//실행됨
	}

}
