
public class Priority {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("2x8-6/2 = "+(2*8-6/2));
		System.out.println("2x(8-6)/2 = "+(2*(8-6)/2));
		System.out.println("1-2+3 = "+(1-2+3));
		System.out.println("1-(2+3) = "+(1-(2+3)));
		
	}
}
